custom_scss:
h1 {
    color: var(--logo-yellow);
    font-family: "Montserrat", sans-serif;
    font-optical-sizing: auto;
    font-weight: 200;
    font-style: italic;
    animation: animation_h1 1s;
}
h2 {
    color: var(--gray-500);
    font-family: "Montserrat", sans-serif;
    font-optical-sizing: auto;
    font-weight: 450;
    font-style: normal;
    animation-name: animation_h2;
    animation-duration: 2s;
}
h3 {
    animation-name: animation_h3;
    animation-duration: 2s;
}

.btn-primary, a, .dropdown-menu {
    transition: 0.3s;
}
.btn-primary:hover, a:hover {
    font-size: var(--text-xl);
}

.navbar-brand, .card {
    transition: transform 0.3s ease;
}
.navbar-brand:hover {
    transform: scale(1.5);
}
.card:hover {
    transform: scale(1.05);
}

.navbar-primary {
    background: linear-gradient(to right, darken($logo-blue, 20%), $logo-blue);
    padding: 12px 0;
    .navbar-search {
		background-color: white;
		color: $gray-700;
		&::placeholder {
			color: $gray-500;
		}
	}
}
.navbar {
	.navbar-nav {
		.nav-item {
			&.active {
				border-bottom: 1px solid var(--logo-yellow);
			}
		}
	}
}

// animations
html {
    scroll-behavior: smooth;
}

@keyframes animation_h1 {
    0% {
        opacity: 0;
        transform: translateY(25px);
    }
    100% {
        opacity: 1;
        transform: translateY(0);
    }
}

@keyframes animation_h2 {
    0% {
        opacity: 0;
        transform: translateX(100px);
    }
    100% {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes animation_h3 {
    0% {
        opacity: 0;
        transform: translateX(-100px);
    }
    100% {
        opacity: 1;
        transform: translateX(0);
    }
}

@keyframes slideUpFadeIn {
    0% {
        opacity: 0;
        transform: scale(0.5);
    }
    100% {
        opacity: 1;
        transform: scale(1);
    }
}

.card.animate {
    animation-name: slideUpFadeIn;
    animation-duration: 2s;
}

js:
$(document).ready(function() {
    // Check if the 'gdprAccepted' cookie is not set
    if (!getCookie('gdprAccepted')) {
        // Show the GDPR banner
        $('#gdpr-banner').show();
    }

    // When the accept button is clicked
    $('#accept-gdpr').click(function() {
        // Set the 'gdprAccepted' cookie to 'true'
        setCookie('gdprAccepted', 'true', 365);

        // Hide the GDPR banner
        $('#gdpr-banner').hide();
    });
});

// Function to get a cookie
function getCookie(cname) {
    var name = cname + "=";
    var decodedCookie = decodeURIComponent(document.cookie);
    var ca = decodedCookie.split(';');
    for(var i = 0; i <ca.length; i++) {
        var c = ca[i];
        while (c.charAt(0) == ' ') {
            c = c.substring(1);
        }
        if (c.indexOf(name) == 0) {
            return c.substring(name.length, c.length);
        }
    }
    return "";
}

// Function to set a cookie
function setCookie(cname, cvalue, exdays) {
    var d = new Date();
    d.setTime(d.getTime() + (exdays*24*60*60*1000));
    var expires = "expires="+ d.toUTCString();
    document.cookie = cname + "=" + cvalue + ";" + expires + ";path=/";
}

// animations
// Select the elements to observe
var elements = document.querySelectorAll('.card');

// Create a new Intersection Observer instance
var observer = new IntersectionObserver(function(entries) {
    // Loop over the entries
    entries.forEach(function(entry) {
        // If the element is in the viewport
        if (entry.isIntersecting) {
            // Add the animation class
            entry.target.classList.add('animate');
        }
    });
});

// Start observing each element
elements.forEach(function(element) {
    observer.observe(element);
});

