@import url("https://fonts.googleapis.com/css2?family=Montserrat:ital,wght@0,100..900;1,100..900&display=swap");
// backward compatibility. deprecated in v15
$font-family-sans-serif: "Montserrat", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif;

// override font stack if custom font is set in website theme
:root {
	--font-stack: "Montserrat", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif !important;
}
$enable-shadows: false;
$enable-gradients: false;
$enable-rounded: true;

