theme_scss:
@import url("https://fonts.googleapis.com/css2?family=Poppins:ital,wght@0,100;0,200;0,300;0,400;0,500;0,600;0,700;0,800;0,900;1,100;1,200;1,300;1,400;1,500;1,600;1,700;1,800;1,900&display=swap");
// backward compatibility. deprecated in v15
$font-family-sans-serif: "Poppins", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif;

// override font stack if custom font is set in website theme
:root {
	--font-stack: "Poppins", "InterVariable", "Inter", -apple-system, BlinkMacSystemFont,
	"Segoe UI", "Roboto", "Oxygen", "Ubuntu", "Cantarell", "Fira Sans",
	"Droid Sans", "Helvetica Neue", sans-serif !important;
}
$enable-shadows: false;
$enable-gradients: false;
$enable-rounded: true;

